@import url('https://fonts.googleapis.com/css2?family=Inter:ital,wght@0,300;0,400;0,500;0,600;0,700;1,300;1,400;1,500;1,600;1,700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Light theme colors */
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222, 47%, 11%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 47%, 11%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 47%, 11%);
  --primary: hsl(18, 100%, 50%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(0, 90%, 55%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(210, 40%, 96.1%);
  --muted-foreground: hsl(215.4, 16.3%, 46.9%);
  --accent: hsl(16, 100%, 60%);
  --accent-foreground: hsl(222, 47%, 11%);
  --destructive: hsl(0, 84.2%, 50%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(214.3, 31.8%, 91.4%);
  --input: hsl(214.3, 31.8%, 91.4%);
  --ring: hsl(18, 100%, 50%);
  --chart-1: hsl(18, 100%, 50%);
  --chart-2: hsl(173, 58%, 45%);
  --chart-3: hsl(197, 37%, 30%);
  --chart-4: hsl(43, 74%, 55%);
  --chart-5: hsl(27, 87%, 57%);
  --sidebar: hsl(0, 0%, 100%);
  --sidebar-foreground: hsl(222, 47%, 11%);
  --sidebar-primary: hsl(18, 100%, 50%);
  --sidebar-primary-foreground: hsl(0, 0%, 98%);
  --sidebar-accent: hsl(210, 40%, 96.1%);
  --sidebar-accent-foreground: hsl(222, 47%, 11%);
  --sidebar-border: hsl(214.3, 31.8%, 91.4%);
  --sidebar-ring: hsl(18, 100%, 50%);
  --font-sans: 'Inter', system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace;
  --radius: 0.75rem;
  --dark: hsl(0, 0%, 3.9%);
}

.dark {
  --background: hsl(0, 0%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --card: hsl(0, 0%, 6.7%);
  --card-foreground: hsl(0, 0%, 98%);
  --popover: hsl(0, 0%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --primary: hsl(18, 100%, 60.2%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(0, 100%, 60%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(0, 0%, 14.9%);
  --muted-foreground: hsl(0, 0%, 63.9%);
  --accent: hsl(16, 100%, 69.8%);
  --accent-foreground: hsl(0, 0%, 9%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(0, 0%, 13.3%);
  --input: hsl(0, 0%, 13.3%);
  --ring: hsl(18, 100%, 60.2%);
  --chart-1: hsl(18, 100%, 60.2%);
  --chart-2: hsl(173, 58%, 39%);
  --chart-3: hsl(197, 37%, 24%);
  --chart-4: hsl(43, 74%, 66%);
  --chart-5: hsl(27, 87%, 67%);
  --sidebar: hsl(0, 0%, 6.7%);
  --sidebar-foreground: hsl(0, 0%, 98%);
  --sidebar-primary: hsl(18, 100%, 60.2%);
  --sidebar-primary-foreground: hsl(0, 0%, 98%);
  --sidebar-accent: hsl(0, 0%, 14.9%);
  --sidebar-accent-foreground: hsl(0, 0%, 98%);
  --sidebar-border: hsl(0, 0%, 13.3%);
  --sidebar-ring: hsl(18, 100%, 60.2%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  .gradient-text {
    background: linear-gradient(135deg, #FF6B35, #FF3333);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-border {
    background: linear-gradient(135deg, #FF6B35, #FF3333, #FF8C69);
    padding: 1px;
    border-radius: 12px;
  }

  .neon-glow {
    box-shadow: 0 0 20px rgba(255, 107, 53, 0.3), 0 0 40px rgba(255, 51, 51, 0.2);
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
}

@keyframes glow {
  from { 
    box-shadow: 0 0 20px rgba(255, 107, 53, 0.3); 
  }
  to { 
    box-shadow: 0 0 30px rgba(255, 107, 53, 0.5); 
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-10px); 
  }
}

@keyframes dissolve {
  0% {
    opacity: 1;
    transform: scale(1);
    filter: blur(0px);
  }
  50% {
    opacity: 0.5;
    transform: scale(0.8);
    filter: blur(2px);
  }
  100% {
    opacity: 0;
    transform: scale(0.5);
    filter: blur(8px);
  }
}

.tag-dissolve {
  animation: dissolve 0.4s ease-out forwards;
}

/* Animated Hover Effects */
.hover-lift {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.hover-lift:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 40px rgba(255, 107, 53, 0.2);
}

.hover-scale {
  transition: transform 0.2s ease-in-out;
}

.hover-scale:hover {
  transform: scale(1.02);
}

.hover-glow {
  transition: all 0.3s ease;
  position: relative;
}

.hover-glow::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, #FF6B35, #FF3333);
  opacity: 0;
  border-radius: inherit;
  transition: opacity 0.3s ease;
  z-index: -1;
}

.hover-glow:hover::before {
  opacity: 0.1;
}

.hover-border-glow {
  transition: all 0.3s ease;
  position: relative;
}

.hover-border-glow::after {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  background: linear-gradient(135deg, #FF6B35, #FF3333, #FF8C69);
  border-radius: inherit;
  opacity: 0;
  z-index: -1;
  transition: opacity 0.3s ease;
}

.hover-border-glow:hover::after {
  opacity: 0.5;
  animation: borderGlowPulse 2s ease-in-out infinite;
}

@keyframes borderGlowPulse {
  0%, 100% {
    opacity: 0.5;
  }
  50% {
    opacity: 0.8;
  }
}

.hover-shine {
  position: relative;
  overflow: hidden;
}

.hover-shine::before {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: linear-gradient(
    45deg,
    transparent 30%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 70%
  );
  transform: rotate(45deg) translateX(-100%);
  transition: transform 0.6s;
}

.hover-shine:hover::before {
  transform: rotate(45deg) translateX(100%);
}

.button-hover {
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.button-hover::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  transform: translate(-50%, -50%);
  transition: width 0.6s, height 0.6s;
}

.button-hover:hover::after {
  width: 300px;
  height: 300px;
}

/* Card hover animations */
.card-hover {
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.card-hover:hover {
  transform: translateY(-8px) scale(1.02);
  box-shadow: 
    0 20px 40px rgba(255, 107, 53, 0.3),
    0 0 0 1px rgba(255, 107, 53, 0.2);
}

/* Neon text glow effect */
.neon-text-glow {
  transition: all 0.3s ease;
}

.neon-text-glow:hover {
  text-shadow: 
    0 0 10px currentColor,
    0 0 20px currentColor,
    0 0 30px currentColor;
}

/* Smooth color transitions */
.color-transition {
  transition: color 0.3s ease, background-color 0.3s ease, border-color 0.3s ease;
}

/* ===== MICRO-INTERACTIONS ===== */

/* Button micro-interactions */
.button-scale {
  transition: transform 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.button-scale:hover {
  transform: scale(1.05);
}

.button-scale:active {
  transform: scale(0.98);
}

/* Smooth fade transitions */
.fade-in {
  animation: fadeIn 0.3s ease-in-out;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Loading skeleton animation */
.skeleton {
  background: linear-gradient(
    90deg,
    hsl(var(--muted)) 0%,
    hsl(var(--muted-foreground) / 0.1) 50%,
    hsl(var(--muted)) 100%
  );
  background-size: 200% 100%;
  animation: skeleton-loading 1.5s ease-in-out infinite;
  border-radius: 4px;
}

@keyframes skeleton-loading {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

/* Progress bar animation */
.progress-animate {
  transition: width 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Card hover lift effect */
.card-lift {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.card-lift:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 24px -10px rgba(0, 0, 0, 0.2);
}

/* Input focus animation */
.input-focus {
  transition: all 0.2s ease;
}

.input-focus:focus {
  transform: scale(1.01);
  box-shadow: 0 0 0 3px hsl(var(--primary) / 0.1);
}

/* Pulse animation for notifications */
@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.9;
  }
}

.pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Smooth number transitions */
.number-transition {
  transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Badge bounce animation */
@keyframes bounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-4px);
  }
}

.bounce-on-hover:hover {
  animation: bounce 0.5s ease-in-out;
}

/* Ripple effect */
.ripple {
  position: relative;
  overflow: hidden;
}

.ripple::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.5);
  transform: translate(-50%, -50%);
  transition: width 0.6s, height 0.6s;
}

.ripple:active::before {
  width: 300px;
  height: 300px;
}

/* Slide in animation */
@keyframes slideIn {
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.slide-in {
  animation: slideIn 0.3s ease-out;
}

/* Spin animation for loading */
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.spin {
  animation: spin 1s linear infinite;
}

/* Shake animation for errors */
@keyframes shake {
  0%, 100% {
    transform: translateX(0);
  }
  10%, 30%, 50%, 70%, 90% {
    transform: translateX(-2px);
  }
  20%, 40%, 60%, 80% {
    transform: translateX(2px);
  }
}

.shake {
  animation: shake 0.5s ease-in-out;
}

/* Smooth expand/collapse */
.expand-collapse {
  transition: height 0.3s ease, opacity 0.3s ease;
  overflow: hidden;
}

