import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { MessageSquare, Heart, Edit, Trash2, Send } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { formatDistanceToNow } from "date-fns";

interface CommentsProps {
  repositoryId: string;
}

export function Comments({ repositoryId }: CommentsProps) {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [newComment, setNewComment] = useState("");
  const [editingCommentId, setEditingCommentId] = useState<number | null>(null);
  const [editContent, setEditContent] = useState("");

  // Fetch comments
  const { data: comments = [], isLoading } = useQuery<any[]>({
    queryKey: [`/api/repositories/${repositoryId}/comments`],
  });

  // Add comment mutation
  const addCommentMutation = useMutation({
    mutationFn: async (content: string) => {
      return await apiRequest("POST", `/api/repositories/${repositoryId}/comments`, {
        content,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/comments`] });
      setNewComment("");
      toast({
        title: "Comment added",
        description: "Your comment has been posted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to add comment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update comment mutation
  const updateCommentMutation = useMutation({
    mutationFn: async ({ commentId, content }: { commentId: number; content: string }) => {
      return await apiRequest("PUT", `/api/comments/${commentId}`, { content });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/comments`] });
      setEditingCommentId(null);
      setEditContent("");
      toast({
        title: "Comment updated",
        description: "Your comment has been updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to update comment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Delete comment mutation
  const deleteCommentMutation = useMutation({
    mutationFn: async (commentId: number) => {
      return await apiRequest("DELETE", `/api/comments/${commentId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/comments`] });
      toast({
        title: "Comment deleted",
        description: "Your comment has been deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to delete comment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Like comment mutation
  const likeCommentMutation = useMutation({
    mutationFn: async (commentId: number) => {
      return await apiRequest("POST", `/api/comments/${commentId}/like`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/comments`] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to like comment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Unlike comment mutation
  const unlikeCommentMutation = useMutation({
    mutationFn: async (commentId: number) => {
      return await apiRequest("DELETE", `/api/comments/${commentId}/like`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/comments`] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to unlike comment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmitComment = () => {
    if (newComment.trim()) {
      addCommentMutation.mutate(newComment);
    }
  };

  const handleUpdateComment = (commentId: number) => {
    if (editContent.trim()) {
      updateCommentMutation.mutate({ commentId, content: editContent });
    }
  };

  const startEditing = (comment: any) => {
    setEditingCommentId(comment.id);
    setEditContent(comment.content);
  };

  const cancelEditing = () => {
    setEditingCommentId(null);
    setEditContent("");
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="h-24 bg-muted rounded-lg animate-pulse" />
        <div className="h-24 bg-muted rounded-lg animate-pulse" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold flex items-center gap-2">
          <MessageSquare className="h-5 w-5" />
          Comments ({comments.length})
        </h3>
      </div>

      {isAuthenticated && (
        <Card className="p-4">
          <div className="space-y-3">
            <Textarea
              placeholder="Share your thoughts about this repository..."
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              className="min-h-[80px]"
              data-testid="input-comment"
            />
            <div className="flex justify-end">
              <Button
                onClick={handleSubmitComment}
                disabled={!newComment.trim() || addCommentMutation.isPending}
                className="flex items-center gap-2"
                data-testid="button-submit-comment"
              >
                <Send className="h-4 w-4" />
                {addCommentMutation.isPending ? "Posting..." : "Post Comment"}
              </Button>
            </div>
          </div>
        </Card>
      )}

      <div className="space-y-4">
        {comments.length === 0 ? (
          <Card className="p-8 text-center text-muted-foreground">
            <MessageSquare className="h-12 w-12 mx-auto mb-4 opacity-50" />
            <p>No comments yet. Be the first to share your thoughts!</p>
          </Card>
        ) : (
          comments.map((comment: any) => (
            <Card key={comment.id} className="p-4">
              <div className="flex items-start gap-3">
                <Avatar className="h-10 w-10">
                  <AvatarImage src={comment.user?.profileImageUrl} />
                  <AvatarFallback>
                    {comment.user?.firstName?.[0] || comment.user?.email?.[0] || "U"}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1">
                  <div className="flex items-center justify-between">
                    <div>
                      <span className="font-semibold">
                        {comment.user?.firstName && comment.user?.lastName
                          ? `${comment.user.firstName} ${comment.user.lastName}`
                          : comment.user?.email?.split("@")[0] || "Anonymous"}
                      </span>
                      <span className="text-sm text-muted-foreground ml-2">
                        {formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
                      </span>
                      {comment.isEdited && (
                        <span className="text-xs text-muted-foreground ml-2">(edited)</span>
                      )}
                    </div>
                    {isAuthenticated && comment.userId === user?.id && (
                      <div className="flex items-center gap-1">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => startEditing(comment)}
                          data-testid={`button-edit-comment-${comment.id}`}
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => deleteCommentMutation.mutate(comment.id)}
                          disabled={deleteCommentMutation.isPending}
                          data-testid={`button-delete-comment-${comment.id}`}
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    )}
                  </div>
                  {editingCommentId === comment.id ? (
                    <div className="mt-2 space-y-2">
                      <Textarea
                        value={editContent}
                        onChange={(e) => setEditContent(e.target.value)}
                        className="min-h-[60px]"
                        data-testid={`input-edit-comment-${comment.id}`}
                      />
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          onClick={() => handleUpdateComment(comment.id)}
                          disabled={updateCommentMutation.isPending}
                          data-testid={`button-save-edit-${comment.id}`}
                        >
                          {updateCommentMutation.isPending ? "Saving..." : "Save"}
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={cancelEditing}
                          data-testid={`button-cancel-edit-${comment.id}`}
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <p className="mt-2 text-sm">{comment.content}</p>
                  )}
                  <div className="flex items-center gap-4 mt-3">
                    <Button
                      variant="ghost"
                      size="sm"
                      className="flex items-center gap-1 h-auto p-1"
                      onClick={() =>
                        comment.hasLiked
                          ? unlikeCommentMutation.mutate(comment.id)
                          : likeCommentMutation.mutate(comment.id)
                      }
                      disabled={
                        !isAuthenticated ||
                        likeCommentMutation.isPending ||
                        unlikeCommentMutation.isPending
                      }
                      data-testid={`button-like-comment-${comment.id}`}
                    >
                      <Heart
                        className={`h-4 w-4 ${comment.hasLiked ? "fill-red-500 text-red-500" : ""}`}
                      />
                      <span className="text-sm">{comment.likeCount || 0}</span>
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/components/comparison-table.t.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface Repository {
  id: string;
  name: string;
  fullName: string;
  description: string | null;
  language: string | null;
  stars: number;
  forks: number;
  watchers: number;
}

interface Analysis {
  originality: number;
  completeness: number;
  marketability: number;
  monetization: number;
  usefulness: number;
  overallScore: number;
}

interface ComparisonTableProps {
  repoA: Repository;
  repoB: Repository;
}

export default function ComparisonTable({ repoA, repoB }: ComparisonTableProps) {
  const { data: dataA } = useQuery({
    queryKey: ['/api/repositories', repoA.id],
    enabled: !!repoA.id,
  });

  const { data: dataB } = useQuery({
    queryKey: ['/api/repositories', repoB.id],
    enabled: !!repoB.id,
  });

  const analysisA = dataA?.analysis;
  const analysisB = dataB?.analysis;

  const compareMetric = (valueA: number | undefined, valueB: number | undefined) => {
    if (!valueA || !valueB) return 'tie';
    if (valueA > valueB) return 'a';
    if (valueB > valueA) return 'b';
    return 'tie';
  };

  const getWinnerIcon = (winner: string) => {
    switch (winner) {
      case 'a':
        return <i className="fas fa-trophy text-primary" data-testid="trophy-repo-a"></i>;
      case 'b':
        return <i className="fas fa-trophy text-accent" data-testid="trophy-repo-b"></i>;
      default:
        return <i className="fas fa-minus text-gray-400" data-testid="tie-icon"></i>;
    }
  };

  const formatNumber = (num: number) => {
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}k`;
    return num.toLocaleString();
  };

  return (
    <Card className="bg-card border border-border">
      <CardContent className="p-8">
        <h3 className="text-2xl font-bold mb-8 text-center gradient-text">Comparison Results</h3>

        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="border-b border-border">
                <th className="text-left py-4 font-medium text-gray-300">Metric</th>
                <th className="text-center py-4 font-medium">
                  <div className="flex flex-col items-center">
                    <span className="text-primary" data-testid="header-repo-a">{repoA.name}</span>
                    <span className="text-xs text-gray-400">{repoA.fullName}</span>
                  </div>
                </th>
                <th className="text-center py-4 font-medium">
                  <div className="flex flex-col items-center">
                    <span className="text-accent" data-testid="header-repo-b">{repoB.name}</span>
                    <span className="text-xs text-gray-400">{repoB.fullName}</span>
                  </div>
                </th>
                <th className="text-center py-4 font-medium text-gray-300">Winner</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-border">
              {/* Basic Repository Metrics */}
              <tr>
                <td className="py-4 font-medium">Stars</td>
                <td className="py-4 text-center">
                  <span className="text-primary font-bold" data-testid="stars-repo-a">
                    {formatNumber(repoA.stars)}
                  </span>
                </td>
                <td className="py-4 text-center">
                  <span className="text-accent font-bold" data-testid="stars-repo-b">
                    {formatNumber(repoB.stars)}
                  </span>
                </td>
