import React, { useState, useEffect } from 'react';
import Joyride, { Step, CallBackProps, STATUS, EVENTS } from 'react-joyride';
import { useLocation } from 'wouter';
import { useToast } from '@/hooks/use-toast';

const tourSteps: Step[] = [
  {
    target: 'body',
    placement: 'center',
    title: 'ðŸš€ Welcome to RepoAnalyzer!',
    content: (
      <div className="space-y-2">
        <p>Your AI-powered GitHub repository analysis platform.</p>
        <p className="text-sm text-gray-600">Let's take a quick tour to help you get started!</p>
      </div>
    ),
    disableBeacon: true,
  },
  {
    target: '[data-tour="search-box"]',
    title: 'ðŸ” Smart Repository Search',
    content: 'Start by searching for any GitHub repository. Just paste a GitHub URL or search by name.',
    placement: 'bottom',
  },
  {
    target: '[data-tour="analyze-button"]', 
    title: 'ðŸ¤– AI-Powered Analysis',
    content: 'Click "Analyze" to get comprehensive insights using our advanced AI. We evaluate originality, completeness, marketability, and more!',
    placement: 'bottom',
  },
  {
    target: '[data-tour="ai-assistant"]',
    title: 'âœ¨ AI Assistant',
    content: 'Need help? Click the AI assistant button for instant, context-aware help with any feature!',
    placement: 'left',
  },
  {
    target: 'body',
    placement: 'center',
    title: 'ðŸŽ‰ You\'re All Set!',
    content: (
      <div className="space-y-3">
        <p>Ready to start analyzing repositories?</p>
        <p className="text-sm text-gray-600">Try searching for your own GitHub repository or explore trending projects!</p>
        <p className="text-xs text-gray-500 mt-3">Tip: You can restart this tour anytime from your profile settings.</p>
      </div>
    ),
  },
];

interface OnboardingTourProps {
  onComplete?: () => void;
}

export default function OnboardingTour({ onComplete }: OnboardingTourProps) {
  const [runTour, setRunTour] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  useEffect(() => {
    // Check if tour has been completed before
    const tourCompleted = localStorage.getItem('completedTour');
    const skipTour = localStorage.getItem('skipTour');

    if (!tourCompleted && !skipTour) {
      // Small delay to ensure page is fully loaded
      setTimeout(() => {
        setRunTour(true);
      }, 1000);
    }
  }, []);

  const handleJoyrideCallback = (data: CallBackProps) => {
    const { status, type } = data;

    if (status === STATUS.FINISHED) {
      // Mark tour as completed
      localStorage.setItem('completedTour', 'true');
      localStorage.setItem('tourCompletedDate', new Date().toISOString());
      setRunTour(false);

      toast({
        title: "Tour Completed! ðŸŽ‰",
        description: "You can restart the tour anytime from your profile settings.",
      });

      if (onComplete) {
        onComplete();
      }
    } else if (status === STATUS.SKIPPED) {
      // Mark as skipped
      localStorage.setItem('skipTour', 'true');
      setRunTour(false);

      toast({
        title: "Tour Skipped",
        description: "You can restart the tour anytime from your profile settings.",
      });
    }
  };

  const joyrideStyles = {
    options: {
      primaryColor: 'rgb(59, 130, 246)', // Blue color matching the app theme
      backgroundColor: '#1f2937',
      textColor: '#ffffff',
      arrowColor: '#1f2937',
      overlayColor: 'rgba(0, 0, 0, 0.7)',
      beaconSize: 36,
      zIndex: 10000,
    },
    tooltip: {
      borderRadius: '8px',
      fontSize: '14px',
    },
    tooltipContainer: {
      textAlign: 'left' as const,
    },
    tooltipTitle: {
      fontSize: '18px',
      marginBottom: '8px',
    },
    tooltipContent: {
      padding: '8px 0',
    },
    buttonNext: {
      backgroundColor: 'rgb(59, 130, 246)',
      borderRadius: '6px',
      color: '#fff',
      fontSize: '14px',
      padding: '8px 16px',
    },
    buttonBack: {
      color: '#94a3b8',
      fontSize: '14px',
      marginRight: '8px',
    },
    buttonSkip: {
      color: '#64748b',
      fontSize: '14px',
    },
    spotlight: {
      borderRadius: '8px',
    },
  };

  if (!runTour) return null;

  return (
    <Joyride
      steps={tourSteps}
      run={runTour}
      continuous
      showProgress
      showSkipButton
      scrollToFirstStep
      scrollOffset={100}
      spotlightPadding={10}
      disableOverlayClose
      disableScrollParentFix
      callback={handleJoyrideCallback}
      styles={joyrideStyles}
      locale={{
        back: 'Back',
        close: 'Close',
        last: 'Finish Tour',
        next: 'Next',
        skip: 'Skip Tour',
      }}
      floaterProps={{
        disableAnimation: false,
        disableFlip: false,
      }}
    />
  );
}

// Export function to restart tour programmatically
export function restartTour() {
  localStorage.removeItem('completedTour');
  localStorage.removeItem('skipTour');
  localStorage.removeItem('tourCompletedDate');
  window.location.reload();
}

client/src/components/progress-indicator.content:
import { useEffect, useState } from 'react';
import { Progress } from '@/components/ui/progress';
import { Card, CardContent } from '@/components/ui/card';

interface ProgressIndicatorProps {
  isAnalyzing: boolean;
}

export function ProgressIndicator({ isAnalyzing }: ProgressIndicatorProps) {
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState('');

  useEffect(() => {
    if (!isAnalyzing) {
      setProgress(0);
      setStatus('');
      return;
    }

    const stages = [
      { progress: 10, status: 'Fetching repository data...', duration: 1000 },
      { progress: 25, status: 'Analyzing code structure...', duration: 2000 },
      { progress: 40, status: 'Evaluating completeness...', duration: 2000 },
      { progress: 55, status: 'Assessing marketability...', duration: 2000 },
      { progress: 70, status: 'Calculating monetization potential...', duration: 2000 },
      { progress: 85, status: 'Generating recommendations...', duration: 2000 },
      { progress: 95, status: 'Finalizing analysis...', duration: 1000 },
    ];

    let currentStage = 0;

    const updateProgress = () => {
      if (currentStage < stages.length) {
        setProgress(stages[currentStage].progress);
        setStatus(stages[currentStage].status);

        setTimeout(() => {
          currentStage++;
          updateProgress();
        }, stages[currentStage].duration);
      }
    };

    updateProgress();
  }, [isAnalyzing]);

  if (!isAnalyzing) return null;

  return (
    <Card className="bg-card/50 border border-border backdrop-blur-sm mt-6">
      <CardContent className="p-6">
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium text-gray-300">{status}</span>
            <span className="text-sm text-gray-400">{progress}%</span>
          </div>
          <Progress value={progress} className="h-2 bg-gray-800">
            <div 
              className="h-full bg-gradient-to-r from-primary to-secondary transition-all duration-500"
              style={{ width: `${progress}%` }}
            />
          </Progress>
        </div>
      </CardContent>
    </Card>
  );
}

