import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Star, ThumbsUp, Edit2, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { formatDistanceToNow } from "date-fns";

interface RatingsProps {
  repositoryId: string;
}

export function Ratings({ repositoryId }: RatingsProps) {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [showRatingForm, setShowRatingForm] = useState(false);
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState("");
  const [hoveredStar, setHoveredStar] = useState(0);

  // Fetch ratings
  const { data: ratingsData, isLoading } = useQuery<any>({
    queryKey: [`/api/repositories/${repositoryId}/ratings`],
  });

  const ratings = ratingsData?.ratings || [];
  const averageRating = ratingsData?.average || 0;
  const totalCount = ratingsData?.count || 0;

  // Check if user has already rated
  const userRating = ratings.find((r: any) => r.userId === user?.id);

  // Add/Update rating mutation
  const submitRatingMutation = useMutation({
    mutationFn: async ({ rating, review }: { rating: number; review: string }) => {
      return await apiRequest("POST", `/api/repositories/${repositoryId}/ratings`, {
        rating,
        review,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/ratings`] });
      setShowRatingForm(false);
      setRating(0);
      setReview("");
      toast({
        title: userRating ? "Rating updated" : "Rating submitted",
        description: "Your rating has been saved successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to submit rating",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Delete rating mutation
  const deleteRatingMutation = useMutation({
    mutationFn: async (ratingId: number) => {
      return await apiRequest("DELETE", `/api/ratings/${ratingId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/ratings`] });
      toast({
        title: "Rating deleted",
        description: "Your rating has been removed successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to delete rating",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Mark rating as helpful mutation
  const markHelpfulMutation = useMutation({
    mutationFn: async (ratingId: number) => {
      return await apiRequest("POST", `/api/ratings/${ratingId}/helpful`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/repositories/${repositoryId}/ratings`] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to mark as helpful",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmitRating = () => {
    if (rating > 0) {
      submitRatingMutation.mutate({ rating, review });
    }
  };

  const startEditingRating = () => {
    if (userRating) {
      setRating(userRating.rating);
      setReview(userRating.review || "");
      setShowRatingForm(true);
    }
  };

  const renderStars = (value: number, interactive = false, size = "h-5 w-5") => {
    return (
      <div className="flex items-center gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`${size} ${
              star <= (interactive ? hoveredStar || value : value)
                ? "fill-yellow-400 text-yellow-400"
                : "text-gray-400"
            } ${interactive ? "cursor-pointer transition-colors" : ""}`}
            onClick={interactive ? () => setRating(star) : undefined}
            onMouseEnter={interactive ? () => setHoveredStar(star) : undefined}
            onMouseLeave={interactive ? () => setHoveredStar(0) : undefined}
            data-testid={interactive ? `star-rating-${star}` : undefined}
          />
        ))}
      </div>
    );
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="h-32 bg-muted rounded-lg animate-pulse" />
        <div className="h-24 bg-muted rounded-lg animate-pulse" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold flex items-center gap-2">
          <Star className="h-5 w-5" />
          Ratings & Reviews
        </h3>
      </div>

      {/* Average Rating Display */}
      <Card className="p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="text-3xl font-bold">{averageRating.toFixed(1)}</div>
            <div>
              {renderStars(Math.round(averageRating))}
              <p className="text-sm text-muted-foreground mt-1">
                Based on {totalCount} {totalCount === 1 ? "review" : "reviews"}
              </p>
            </div>
          </div>
          {isAuthenticated && !userRating && !showRatingForm && (
            <Button
              onClick={() => setShowRatingForm(true)}
              className="flex items-center gap-2"
              data-testid="button-add-rating"
            >
              <Star className="h-4 w-4" />
              Add Rating
            </Button>
          )}
          {isAuthenticated && userRating && !showRatingForm && (
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                onClick={startEditingRating}
                className="flex items-center gap-2"
                data-testid="button-edit-rating"
              >
                <Edit2 className="h-4 w-4" />
                Edit Your Rating
              </Button>
              <Button
                variant="outline"
                onClick={() => deleteRatingMutation.mutate(userRating.id)}
                disabled={deleteRatingMutation.isPending}
                className="flex items-center gap-2"
                data-testid="button-delete-rating"
              >
                <Trash2 className="h-4 w-4" />
                Delete
              </Button>
            </div>
          )}
        </div>
      </Card>

      {/* Rating Form */}
      {showRatingForm && (
        <Card className="p-6">
          <h4 className="font-semibold mb-4">
            {userRating ? "Edit Your Rating" : "Rate This Repository"}
          </h4>
          <div className="space-y-4">
            <div>
              <label className="text-sm font-medium mb-2 block">Your Rating</label>
              {renderStars(rating, true, "h-8 w-8")}
            </div>
            <div>
              <label className="text-sm font-medium mb-2 block">Your Review (Optional)</label>
              <Textarea
                placeholder="Share your experience with this repository..."
                value={review}
                onChange={(e) => setReview(e.target.value)}
                className="min-h-[100px]"
                data-testid="input-review"
              />
            </div>
            <div className="flex gap-2">
              <Button
                onClick={handleSubmitRating}
                disabled={rating === 0 || submitRatingMutation.isPending}
                data-testid="button-submit-rating"
              >
                {submitRatingMutation.isPending 
                  ? "Submitting..." 
                  : userRating 
                  ? "Update Rating" 
                  : "Submit Rating"}
              </Button>
              <Button
                variant="outline"
                onClick={() => {
                  setShowRatingForm(false);
                  setRating(0);
                  setReview("");
                }}
                data-testid="button-cancel-rating"
              >
                Cancel
              </Button>
            </div>
          </div>
        </Card>
      )}

      {/* Ratings List */}
      <div className="space-y-4">
        {ratings.length === 0 ? (
          <Card className="p-8 text-center text-muted-foreground">
            <Star className="h-12 w-12 mx-auto mb-4 opacity-50" />
            <p>No ratings yet. Be the first to rate this repository!</p>
          </Card>
        ) : (
          ratings.map((rating: any) => (
            <Card key={rating.id} className="p-4">
              <div className="flex items-start gap-3">
                <Avatar className="h-10 w-10">
                  <AvatarImage src={rating.user?.profileImageUrl} />
                  <AvatarFallback>
                    {rating.user?.firstName?.[0] || rating.user?.email?.[0] || "U"}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1">
                  <div className="flex items-center justify-between">
                    <div>
                      <span className="font-semibold">
                        {rating.user?.firstName && rating.user?.lastName
                          ? `${rating.user.firstName} ${rating.user.lastName}`
                          : rating.user?.email?.split("@")[0] || "Anonymous"}
                      </span>
                      <span className="text-sm text-muted-foreground ml-2">
                        {formatDistanceToNow(new Date(rating.createdAt), { addSuffix: true })}
                      </span>
                    </div>
                    {renderStars(rating.rating)}
                  </div>
                  {rating.review && (
                    <p className="mt-2 text-sm">{rating.review}</p>
                  )}
                  <div className="flex items-center gap-4 mt-3">
                    <Button
                      variant="ghost"
                      size="sm"
                      className="flex items-center gap-1 h-auto p-1"
                      onClick={() => markHelpfulMutation.mutate(rating.id)}
                      disabled={!isAuthenticated || markHelpfulMutation.isPending}
                      data-testid={`button-helpful-${rating.id}`}
                    >
                      <ThumbsUp className="h-4 w-4" />
                      <span className="text-sm">
                        Helpful ({rating.helpfulCount || 0})
                      </span>
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/components/repository-card.ts.content:
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import type { Repository, RepositoryAnalysis } from "@shared/schema";
import { TrackRepositoryButton } from "@/components/track-repository-button";
import { getScoreHealthIndicator, getMetricHealthIndicator } from "@/utils/health-indicators";

interface RepositoryCardProps {
  repository: Repository;
  analysis?: RepositoryAnalysis;
  showAnalysis?: boolean;
}

export default function RepositoryCard({ repository, analysis, showAnalysis = true }: RepositoryCardProps) {
  const getScoreColor = (score: number) => {
    if (score >= 8) return "text-green-400";
    if (score >= 6) return "text-yellow-400";
    return "text-red-400";
  };

  const getScoreGradient = (score: number) => {
    if (score >= 8) return "from-green-400 to-blue-500";
    if (score >= 6) return "from-yellow-400 to-orange-500";
    return "from-red-400 to-pink-500";
  };

  const handleTrackClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
  };

  return (
    <Link href={`/repository/${repository.id}`}>
      <Card className="bg-card border border-border hover:border-primary/30 transition-all duration-300 group cursor-pointer h-full card-hover card-lift fade-in">
        <CardContent className="p-6">
          <div className="flex items-start justify-between mb-4">
            <div className="flex items-center space-x-3 flex-1 min-w-0">
              <div className="w-10 h-10 rounded-lg bg-gradient-to-r from-blue-500 to-purple-600 flex items-center justify-center flex-shrink-0">
                <i className="fab fa-github text-white"></i>
              </div>
              <div className="min-w-0 flex-1">
                <h4 className="font-semibold text-lg truncate">{repository.name}</h4>
                <p className="text-sm text-gray-400 truncate">{repository.fullName}</p>
              </div>
            </div>
            {showAnalysis && analysis && (
              <div className="flex items-center space-x-1">
                <span className="text-xl">{getScoreHealthIndicator(analysis.overallScore * 10).emoji}</span>
                <div className={`text-2xl font-bold ${getScoreColor(analysis.overallScore)}`}>
                  {analysis.overallScore.toFixed(1)}
                </div>
              </div>
            )}
          </div>

          <p className="text-gray-300 text-sm mb-4 line-clamp-2">
            {repository.description || 'No description available'}
          </p>

          {showAnalysis && analysis && (
            <div className="space-y-2 mb-4">
              <div className="flex items-center justify-between text-xs">
                <span className="text-gray-400">Originality</span>
                <div className="flex items-center space-x-2">
                  <div className="w-16 h-1.5 bg-gray-700 rounded-full overflow-hidden">
                    <div 
                      className={`h-full bg-gradient-to-r ${getScoreGradient(analysis.originality)}`}
                      style={{ width: `${(analysis.originality / 10) * 100}%` }}
                    ></div>
                  </div>
                  <span className={`font-semibold ${getScoreColor(analysis.originality)}`}>
                    {analysis.originality.toFixed(1)}
                  </span>
                </div>
              </div>
              <div className="flex items-center justify-between text-xs">
                <span className="text-gray-400">Completeness</span>
                <div className="flex items-center space-x-2">
                  <div className="w-16 h-1.5 bg-gray-700 rounded-full overflow-hidden">
                    <div 
                      className={`h-full bg-gradient-to-r ${getScoreGradient(analysis.completeness)}`}
                      style={{ width: `${(analysis.completeness / 10) * 100}%` }}
                    ></div>
                  </div>
                  <span className={`font-semibold ${getScoreColor(analysis.completeness)}`}>
                    {analysis.completeness.toFixed(1)}
                  </span>
                </div>
              </div>
              <div className="flex items-center justify-between text-xs">
                <span className="text-gray-400">Marketability</span>
                <div className="flex items-center space-x-2">
                  <div className="w-16 h-1.5 bg-gray-700 rounded-full overflow-hidden">
                    <div 
                      className={`h-full bg-gradient-to-r ${getScoreGradient(analysis.marketability)}`}
                      style={{ width: `${(analysis.marketability / 10) * 100}%` }}
                    ></div>
                  </div>
                  <span className={`font-semibold ${getScoreColor(analysis.marketability)}`}>
                    {analysis.marketability.toFixed(1)}
                  </span>
                </div>
              </div>
            </div>
          )}

          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 text-sm text-gray-400">
              <div className="flex items-center space-x-1">
                <i className="fas fa-star text-yellow-500"></i>
                <span>{repository.stars?.toLocaleString() || 0}</span>
              </div>
              <div className="flex items-center space-x-1">
                <i className="fas fa-code-branch text-blue-400"></i>
                <span>{repository.forks?.toLocaleString() || 0}</span>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              {repository.language && (
                <Badge variant="secondary" className="text-xs">
                  {repository.language}
                </Badge>
              )}
              <div onClick={handleTrackClick}>
                <TrackRepositoryButton 
                  repositoryId={repository.id} 
                  repositoryName={repository.fullName}
                />
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </Link>
  );
}

client/src/components/repository-list-it.content:
import { Star, GitFork, Eye, Calendar, Code, ExternalLink } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { motion } from "framer-motion";
import { format } from "date-fns";

interface RepositoryListItemProps {
  repository: any;
  similarity?: number;
  index: number;
  onAnalyze?: () => void;
}

export default function RepositoryListItem({ 
  repository, 
  similarity, 
  index,
  onAnalyze 
}: RepositoryListItemProps) {
  const repo = repository.repository || repository;

  const formatDate = (date: string) => {
    try {
      return format(new Date(date), 'MMM dd, yyyy');
    } catch {
      return 'N/A';
    }
  };

  const formatNumber = (num: number) => {
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}k`;
    return num.toString();
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: index * 0.05 }}
    >
      <Card className="bg-card/50 border border-border hover:border-[#FF6B35]/50 transition-all duration-200 p-4">
        <div className="flex items-start justify-between gap-4">
          {/* Main Content */}
          <div className="flex-1">
            <div className="flex items-start gap-3">
              {/* Similarity Score */}
              {similarity !== undefined && (
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 rounded-full bg-gradient-to-r from-[#FF6B35] to-[#FF3333] flex items-center justify-center text-white font-bold">
                    {similarity}%
                  </div>
                </div>
              )}

              {/* Repository Info */}
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-1">
                  <h3 className="text-lg font-semibold hover:text-[#FF6B35] transition-colors">
                    <a href={repo.htmlUrl} target="_blank" rel="noopener noreferrer" className="flex items-center gap-1">
                      {repo.fullName}
                      <ExternalLink className="w-4 h-4" />
                    </a>
                  </h3>
                  {repo.language && (
                    <Badge variant="outline" className="text-xs">
                      <Code className="w-3 h-3 mr-1" />
                      {repo.language}
                    </Badge>
                  )}
                </div>

                <p className="text-sm text-gray-400 mb-2 line-clamp-2">
                  {repo.description || 'No description available'}
                </p>

                {/* Topics */}
                {repo.topics && repo.topics.length > 0 && (
                  <div className="flex flex-wrap gap-1 mb-2">
                    {repo.topics.slice(0, 5).map((topic: string) => (
                      <Badge key={topic} variant="secondary" className="text-xs bg-[#FF6B35]/10 text-[#FF6B35] border-[#FF6B35]/20">
                        {topic}
                      </Badge>
                    ))}
                    {repo.topics.length > 5 && (
                      <Badge variant="secondary" className="text-xs">
                        +{repo.topics.length - 5} more
                      </Badge>
                    )}
                  </div>
                )}

                {/* Stats Row */}
                <div className="flex items-center gap-4 text-sm text-gray-500">
                  <span className="flex items-center gap-1">
                    <Star className="w-4 h-4 text-yellow-500" />
                    {formatNumber(repo.stars || 0)}
                  </span>
                  <span className="flex items-center gap-1">
                    <GitFork className="w-4 h-4" />
                    {formatNumber(repo.forks || 0)}
                  </span>
                  <span className="flex items-center gap-1">
                    <Eye className="w-4 h-4" />
                    {formatNumber(repo.watchers || 0)}
                  </span>
                  <span className="flex items-center gap-1">
                    <Calendar className="w-4 h-4" />
                    Updated {formatDate(repo.updatedAt)}
                  </span>
                  {repo.size && (
                    <span className="text-xs">
                      {(repo.size / 1024).toFixed(1)} MB
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col gap-2">
            <Button
              size="sm"
              variant="outline"
              className="border-[#FF6B35]/30 hover:border-[#FF6B35] hover:bg-[#FF6B35]/10"
              onClick={onAnalyze}
              data-testid={`button-analyze-${repo.id}`}
            >
              Analyze
            </Button>
            <a href={repo.htmlUrl} target="_blank" rel="noopener noreferrer">
              <Button
                size="sm"
                variant="ghost"
                className="w-full"
                data-testid={`button-view-${repo.id}`}
              >
                View on GitHub
              </Button>
            </a>
          </div>
        </div>
      </Card>
    </motion.div>
  );
}

client/src/components/search-section.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";

export default function SearchSection() {
  const [, setLocation] = useLocation();
  const [query, setQuery] = useState("");
  const [url, setUrl] = useState("");

  const handleSearch = () => {
    if (query.trim()) {
      setLocation(`/search?q=${encodeURIComponent(query.trim())}`);
    }
  };

  const handleAnalyze = () => {
    if (url.trim()) {
      setLocation(`/analyze?url=${encodeURIComponent(url.trim())}`);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent, action: () => void) => {
    if (e.key === 'Enter') {
      action();
    }
  };

  return (
    <section className="py-20 bg-gradient-to-b from-dark via-card to-dark">
      <div className="max-w-4xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-4">Discover & Analyze</h2>
        <p className="text-xl text-gray-300 mb-12">
          Search repositories or analyze specific ones with AI-powered insights
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Search Repositories */}
          <Card className="bg-card border border-border hover-lift hover-border-glow">
            <CardContent className="p-8">
              <div className="w-12 h-12 rounded-lg bg-gradient-to-r from-primary to-secondary flex items-center justify-center mx-auto mb-6">
                <i className="fas fa-search text-white"></i>
              </div>
              <h3 className="text-2xl font-semibold mb-4">Search Repositories</h3>
              <p className="text-gray-400 mb-6">
                Discover repositories by keywords, topics, or technology
              </p>
              <div className="flex space-x-3">
                <Input
                  type="text"
                  placeholder="Search repositories..."
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  onKeyPress={(e) => handleKeyPress(e, handleSearch)}
                  className="flex-1 bg-dark border-border text-white input-focus"
                  data-testid="input-search"
                  data-tour="search-box"
                />
                <Button
                  onClick={handleSearch}
                  className="bg-primary hover:bg-primary/80 text-white button-hover hover-shine button-scale ripple"
                  data-testid="button-search"
                >
                  Search
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Analyze Repository */}
          <Card className="bg-card border border-border hover-lift hover-border-glow">
            <CardContent className="p-8">
              <div className="w-12 h-12 rounded-lg bg-gradient-to-r from-accent to-primary flex items-center justify-center mx-auto mb-6">
                <i className="fas fa-robot text-white"></i>
              </div>
              <h3 className="text-2xl font-semibold mb-4">Analyze Repository</h3>
              <p className="text-gray-400 mb-6">
                Get AI-powered analysis of any GitHub repository
              </p>
              <div className="flex space-x-3">
                <Input
                  type="text"
                  placeholder="GitHub repository URL..."
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  onKeyPress={(e) => handleKeyPress(e, handleAnalyze)}
                  className="flex-1 bg-dark border-border text-white input-focus"
                  data-testid="input-analyze"
                />
                <Button
                  onClick={handleAnalyze}
                  className="bg-gradient-to-r from-accent to-primary hover:from-primary hover:to-accent text-white button-hover hover-shine button-scale ripple"
                  data-testid="button-analyze"
                  data-tour="analyze-button"
                >
                  Analyze
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </section>
  );
}

client/src/components/skeleton-loader.ts.content:
import React from 'react';
import { cn } from '@/lib/utils';

interface SkeletonLoaderProps {
  className?: string;
  count?: number;
  height?: string;
  width?: string;
}

export const SkeletonLoader: React.FC<SkeletonLoaderProps> = ({
  className,
  count = 1,
  height = '20px',
  width = '100%',
}) => {
  return (
    <div className={cn('space-y-3 fade-in', className)}>
      {Array.from({ length: count }).map((_, index) => (
        <div
          key={index}
          className="skeleton"
          style={{
            height,
            width,
          }}
        />
      ))}
    </div>
  );
};

export const CardSkeleton: React.FC<{ className?: string }> = ({ className }) => {
  return (
    <div className={cn('p-6 rounded-lg border border-border bg-card fade-in', className)}>
      <div className="skeleton h-6 w-3/4 mb-4" />
      <div className="skeleton h-4 w-full mb-2" />
      <div className="skeleton h-4 w-5/6 mb-4" />
      <div className="flex gap-4 mt-4">
        <div className="skeleton h-8 w-20" />
        <div className="skeleton h-8 w-20" />
        <div className="skeleton h-8 w-20" />
      </div>
    </div>
  );
};

export const TableSkeleton: React.FC<{ rows?: number; className?: string }> = ({ 
  rows = 5, 
  className 
}) => {
  return (
    <div className={cn('w-full fade-in', className)}>
      <div className="skeleton h-12 w-full mb-2" />
      {Array.from({ length: rows }).map((_, index) => (
        <div key={index} className="skeleton h-16 w-full mb-2" />
      ))}
    </div>
  );
};

export const ButtonSkeleton: React.FC<{ className?: string }> = ({ className }) => {
  return <div className={cn('skeleton h-10 w-24 rounded-md', className)} />;
};

client/src/components/theme-toggle.tsx.content:
import { Moon, Sun } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useTheme } from '@/contexts/theme-context';

export function ThemeToggle() {
