                              </div>
                              <span className={`font-bold text-lg ${getScoreColor(score)} min-w-[3rem] text-right`}>
                                {score?.toFixed(1)}
                              </span>
                            </div>
                          </div>
                          {analysis?.scoreExplanations?.[key] && (
                            <p className="text-sm text-gray-400 pl-4 border-l-2 border-gray-700">
                              {analysis.scoreExplanations[key]}
                            </p>
                          )}
