                      </div>

                      <div>
                        <h4 className="text-lg font-semibold mb-3">Recommendations</h4>
                        <ul className="space-y-4">
                          {analysis?.recommendations?.map((recommendation: any, index: number) => (
                            <li key={index} className="space-y-2">
