} from "@/components/ui/accordion";

interface CodeIssue {
  type: 'error' | 'warning' | 'suggestion' | 'security';
  severity: 'critical' | 'high' | 'medium' | 'low';
  line: number;
  column: number;
  message: string;
  suggestion?: string;
  file: string;
  category: string;
}

interface ReviewResult {
  overallScore: number;
  codeQuality: number;
  security: number;
  performance: number;
  maintainability: number;
  testCoverage: number;
  issues: CodeIssue[];
  suggestions: string[];
  positives: string[];
  metrics: {
    linesOfCode: number;
    complexity: number;
    duplications: number;
    technicalDebt: string;
  };
}

export default function CodeReview() {
