import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { 
  User, Settings, Bookmark, Tag, FolderOpen, Brain, 
  Plus, Trash2, Edit, Star, GitBranch, Calendar, 
  TrendingUp, Code, Package, Sparkles, Lock, Crown
} from "lucide-react";
import { Link } from "wouter";
import { motion } from "framer-motion";
import { restartTour } from "@/components/onboarding-tour";

export default function Profile() {
  const { user, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedCollection, setSelectedCollection] = useState<number | null>(null);
  const [newTagName, setNewTagName] = useState("");
  const [newTagColor, setNewTagColor] = useState("#FF6B35");
  const [newCollectionName, setNewCollectionName] = useState("");
  const [newCollectionDescription, setNewCollectionDescription] = useState("");

  // Check if user has Pro or Enterprise subscription
  const isPremium = user?.subscriptionTier === 'pro' || user?.subscriptionTier === 'enterprise';

  // Fetch user preferences
  const { data: preferences, isLoading: preferencesLoading } = useQuery<any>({
    queryKey: ['/api/user/preferences'],
    enabled: isPremium,
    retry: false,
  });

  // Fetch bookmarks
  const { data: bookmarks = [] } = useQuery<any[]>({
    queryKey: ['/api/user/bookmarks'],
    enabled: isPremium,
    retry: false,
  });

  // Fetch tags
  const { data: tags = [] } = useQuery<any[]>({
    queryKey: ['/api/user/tags'],
    enabled: isPremium,
    retry: false,
  });

  // Fetch collections
  const { data: collections = [] } = useQuery<any[]>({
    queryKey: ['/api/user/collections'],
    enabled: isPremium,
    retry: false,
  });

  // Fetch AI recommendations
  const { data: recommendations, isLoading: recommendationsLoading } = useQuery<any>({
    queryKey: ['/api/user/recommendations'],
    enabled: isPremium && preferences?.aiRecommendations !== false,
    retry: false,
  });

  // Update preferences mutation
  const updatePreferencesMutation = useMutation({
    mutationFn: async (prefs: any) => {
      return await apiRequest('PUT', '/api/user/preferences', prefs);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/preferences'] });
      toast({
        title: "Preferences Updated",
        description: "Your preferences have been saved successfully.",
      });
    },
  });

  // Create tag mutation
  const createTagMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('POST', '/api/user/tags', {
        name: newTagName,
        color: newTagColor,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/tags'] });
      setNewTagName("");
      toast({
        title: "Tag Created",
        description: "Your new tag has been created successfully.",
      });
    },
  });

  // Create collection mutation
  const createCollectionMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('POST', '/api/user/collections', {
        name: newCollectionName,
        description: newCollectionDescription,
        icon: "folder",
        color: "#FF6B35",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/collections'] });
      setNewCollectionName("");
      setNewCollectionDescription("");
      toast({
        title: "Collection Created",
        description: "Your new collection has been created successfully.",
      });
    },
  });

  // Remove bookmark mutation
  const removeBookmarkMutation = useMutation({
    mutationFn: async (repositoryId: string) => {
      return await apiRequest('DELETE', `/api/user/bookmarks/${repositoryId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/bookmarks'] });
      toast({
        title: "Bookmark Removed",
        description: "Repository has been removed from your bookmarks.",
      });
    },
  });

  if (authLoading || preferencesLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!user) {
    useEffect(() => {
      toast({
        title: "Authentication Required",
        description: "Please log in to view your profile.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }, []);
    return null;
  }

  if (!isPremium) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card className="max-w-2xl mx-auto">
          <CardHeader className="text-center">
            <div className="mb-4">
              <Lock className="w-16 h-16 mx-auto text-muted-foreground" />
            </div>
            <CardTitle className="text-2xl">Premium Feature</CardTitle>
            <CardDescription className="text-lg mt-2">
              Intelligent user profiles are available for Pro and Enterprise users
            </CardDescription>
          </CardHeader>
          <CardContent className="text-center">
            <p className="mb-6 text-muted-foreground">
              Unlock AI-powered recommendations, smart bookmarks, tags, and collections to organize and discover repositories tailored to your interests.
            </p>
            <div className="flex gap-4 justify-center">
              <Link href="/pricing">
                <Button size="lg" className="gap-2">
                  <Crown className="w-5 h-5" />
                  View Plans
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-7xl mx-auto">
        {/* User Header */}
        <Card className="mb-8 bg-gradient-to-r from-primary/10 to-primary/5">
          <CardContent className="p-6">
            <div className="flex items-center gap-6">
              {user.profileImageUrl ? (
                <img 
                  src={user.profileImageUrl} 
                  alt={user.firstName || "Profile"} 
                  className="w-20 h-20 rounded-full object-cover border-4 border-primary/20"
                />
              ) : (
                <div className="w-20 h-20 rounded-full bg-primary/20 flex items-center justify-center">
                  <User className="w-10 h-10 text-primary" />
                </div>
              )}
              <div className="flex-1">
                <h1 className="text-3xl font-bold flex items-center gap-2">
                  {user.firstName} {user.lastName}
                  <Badge variant="default" className="ml-2">
                    {user.subscriptionTier === 'enterprise' ? 'Enterprise' : 'Pro'}
                  </Badge>
                </h1>
                <p className="text-muted-foreground">{user.email}</p>
                <div className="flex gap-4 mt-2 text-sm text-muted-foreground">
                  <span className="flex items-center gap-1">
                    <Calendar className="w-4 h-4" />
                    Member since {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
                  </span>
                  <span className="flex items-center gap-1">
                    <TrendingUp className="w-4 h-4" />
                    {bookmarks.length} Bookmarks
                  </span>
                  <span className="flex items-center gap-1">
                    <FolderOpen className="w-4 h-4" />
                    {collections.length} Collections
                  </span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Subscription Status Card */}
        <Card className="mb-6 border-2 border-primary/20">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span className="flex items-center gap-2">
                <Crown className="h-5 w-5" />
                Subscription Details
              </span>
              <Link href="/pricing">
                <Button variant="outline" size="sm">
                  Manage Subscription
                </Button>
              </Link>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid gap-4 md:grid-cols-4">
              <div>
                <p className="text-sm text-muted-foreground">Current Plan</p>
                <p className="text-lg font-semibold capitalize">
                  {user?.subscriptionTier || 'Free'}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Status</p>
                <p className="text-lg font-semibold">
                  {user?.subscriptionStatus === 'active' ? (
                    <span className="text-green-500">Active</span>
                  ) : user?.subscriptionStatus === 'past_due' ? (
                    <span className="text-orange-500">Past Due</span>
                  ) : user?.subscriptionStatus === 'cancelled' ? (
                    <span className="text-red-500">Cancelled</span>
                  ) : (
                    <span className="text-gray-500">Active</span>
                  )}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Daily Analyses</p>
                <p className="text-lg font-semibold">
                  {user?.subscriptionTier === 'enterprise' ? 'Unlimited' : user?.subscriptionTier === 'pro' ? '50 per day' : 'Unlimited'}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">API Access</p>
                <p className="text-lg font-semibold">
                  {user?.subscriptionTier === 'enterprise' ? '10K/hour' : '1K/hour'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Main Content Tabs */}
        <Tabs defaultValue="recommendations" className="space-y-6">
          <TabsList className="grid w-full grid-cols-5">
            <TabsTrigger value="recommendations" className="gap-2">
              <Brain className="w-4 h-4" />
              AI Recommendations
            </TabsTrigger>
            <TabsTrigger value="bookmarks" className="gap-2">
              <Bookmark className="w-4 h-4" />
              Bookmarks
            </TabsTrigger>
            <TabsTrigger value="collections" className="gap-2">
              <FolderOpen className="w-4 h-4" />
              Collections
            </TabsTrigger>
            <TabsTrigger value="tags" className="gap-2">
              <Tag className="w-4 h-4" />
              Tags
            </TabsTrigger>
            <TabsTrigger value="preferences" className="gap-2">
              <Settings className="w-4 h-4" />
              Preferences
            </TabsTrigger>
          </TabsList>

          {/* AI Recommendations Tab */}
          <TabsContent value="recommendations">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Sparkles className="w-5 h-5 text-primary" />
                  Personalized Recommendations
                </CardTitle>
                <CardDescription>
                  AI-curated repositories based on your activity and preferences
                </CardDescription>
              </CardHeader>
              <CardContent>
                {recommendationsLoading ? (
                  <div className="flex justify-center py-8">
                    <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
                  </div>
                ) : recommendations?.recommendations?.length > 0 ? (
                  <div className="space-y-4">
                    {recommendations.recommendations.map((repo: any, index: number) => (
                      <motion.div
                        key={repo.name}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="p-4 border rounded-lg hover:bg-accent/50 transition-colors"
                      >
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <h3 className="font-semibold text-lg flex items-center gap-2">
                              <GitBranch className="w-4 h-4" />
                              {repo.name}
                            </h3>
                            <p className="text-sm text-muted-foreground mt-1">
                              {repo.description}
                            </p>
                            <div className="flex items-center gap-4 mt-2">
                              <Badge variant="secondary">{repo.primaryLanguage}</Badge>
                              <span className="flex items-center gap-1 text-sm">
                                <Star className="w-4 h-4" />
                                {repo.stars}
                              </span>
                              <Badge variant="outline" className="text-xs">
                                {Math.round(repo.matchScore * 100)}% Match
                              </Badge>
                            </div>
                            <p className="text-sm text-primary mt-2">
                              {repo.reason}
                            </p>
                          </div>
                          <Button size="sm" variant="outline" asChild>
                            <Link href={`/repository/${encodeURIComponent(repo.name)}`}>
                              View
                            </Link>
                          </Button>
                        </div>
                      </motion.div>
                    ))}

                    {recommendations?.insights && (
                      <Card className="mt-6 bg-primary/5">
                        <CardHeader>
                          <CardTitle className="text-lg">Insights</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-2">
                          <div>
                            <span className="font-semibold">Top Interests:</span>{" "}
                            {recommendations.insights.topInterests?.join(", ")}
                          </div>
                          <div>
                            <span className="font-semibold">Suggested Topics:</span>{" "}
                            {recommendations.insights.suggestedTopics?.join(", ")}
                          </div>
                          <div className="text-sm text-muted-foreground mt-2">
                            {recommendations.insights.recommendationRationale}
                          </div>
                        </CardContent>
                      </Card>
                    )}
                  </div>
                ) : (
                  <p className="text-center text-muted-foreground py-8">
                    No recommendations available yet. Start exploring repositories to get personalized suggestions!
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Bookmarks Tab */}
          <TabsContent value="bookmarks">
            <Card>
              <CardHeader>
                <CardTitle>Bookmarked Repositories</CardTitle>
                <CardDescription>
                  Your saved repositories for quick access
                </CardDescription>
              </CardHeader>
              <CardContent>
                {bookmarks.length > 0 ? (
                  <div className="space-y-3">
                    {bookmarks.map((bookmark: any) => (
                      <div key={bookmark.id} className="flex items-center justify-between p-3 border rounded-lg">
                        <div className="flex-1">
                          <h4 className="font-medium">{bookmark.repositoryId}</h4>
                          {bookmark.notes && (
                            <p className="text-sm text-muted-foreground">{bookmark.notes}</p>
                          )}
                        </div>
                        <div className="flex gap-2">
                          <Button size="sm" variant="outline" asChild>
                            <Link href={`/repository/${bookmark.repositoryId}`}>View</Link>
                          </Button>
                          <Button 
                            size="sm" 
                            variant="ghost"
                            onClick={() => removeBookmarkMutation.mutate(bookmark.repositoryId)}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-center text-muted-foreground py-8">
                    No bookmarks yet. Save repositories to access them quickly!
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Collections Tab */}
          <TabsContent value="collections">
            <Card>
              <CardHeader>
                <CardTitle>Repository Collections</CardTitle>
                <CardDescription>
                  Organize repositories into themed collections
                </CardDescription>
                <div className="mt-4">
                  <Dialog>
                    <DialogTrigger asChild>
                      <Button className="gap-2">
                        <Plus className="w-4 h-4" />
                        New Collection
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Create New Collection</DialogTitle>
                        <DialogDescription>
                          Create a collection to organize related repositories
                        </DialogDescription>
                      </DialogHeader>
                      <div className="space-y-4">
                        <div>
                          <Label htmlFor="collection-name">Name</Label>
                          <Input
                            id="collection-name"
                            value={newCollectionName}
                            onChange={(e) => setNewCollectionName(e.target.value)}
                            placeholder="e.g., Machine Learning Projects"
                          />
                        </div>
                        <div>
                          <Label htmlFor="collection-description">Description</Label>
                          <Textarea
                            id="collection-description"
                            value={newCollectionDescription}
                            onChange={(e) => setNewCollectionDescription(e.target.value)}
                            placeholder="Description of this collection..."
                          />
                        </div>
                        <Button 
                          onClick={() => createCollectionMutation.mutate()}
                          disabled={!newCollectionName}
                          className="w-full"
                        >
                          Create Collection
                        </Button>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>
              </CardHeader>
              <CardContent>
                {collections.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {collections.map((collection: any) => (
                      <Card key={collection.id} className="hover:shadow-lg transition-shadow">
                        <CardHeader>
                          <CardTitle className="text-lg flex items-center gap-2">
                            <FolderOpen className="w-5 h-5" style={{ color: collection.color }} />
                            {collection.name}
                          </CardTitle>
                          {collection.description && (
                            <CardDescription>{collection.description}</CardDescription>
                          )}
                        </CardHeader>
                        <CardContent>
                          <div className="flex justify-between items-center">
                            <span className="text-sm text-muted-foreground">
                              {collection.isPublic ? "Public" : "Private"}
                            </span>
                            <Button size="sm" variant="outline">
                              View Collection
                            </Button>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                ) : (
                  <p className="text-center text-muted-foreground py-8">
                    No collections yet. Create collections to organize your repositories!
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Tags Tab */}
          <TabsContent value="tags">
            <Card>
              <CardHeader>
                <CardTitle>Repository Tags</CardTitle>
                <CardDescription>
                  Create tags to categorize and filter repositories
                </CardDescription>
                <div className="mt-4 flex gap-2">
                  <Input
                    placeholder="Tag name..."
                    value={newTagName}
                    onChange={(e) => setNewTagName(e.target.value)}
                    className="max-w-xs"
                  />
                  <Input
                    type="color"
                    value={newTagColor}
                    onChange={(e) => setNewTagColor(e.target.value)}
                    className="w-20"
                  />
                  <Button 
                    onClick={() => createTagMutation.mutate()}
                    disabled={!newTagName}
                    className="gap-2"
                  >
                    <Plus className="w-4 h-4" />
                    Add Tag
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                {tags.length > 0 ? (
                  <div className="flex flex-wrap gap-2">
                    {tags.map((tag: any) => (
                      <Badge 
                        key={tag.id} 
                        variant="outline" 
                        className="px-3 py-1"
                        style={{ borderColor: tag.color, color: tag.color }}
                      >
                        {tag.name}
                      </Badge>
                    ))}
                  </div>
                ) : (
                  <p className="text-center text-muted-foreground py-8">
                    No tags yet. Create tags to categorize your repositories!
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Preferences Tab */}
          <TabsContent value="preferences">
            <Card>
              <CardHeader>
                <CardTitle>Profile Preferences</CardTitle>
                <CardDescription>
                  Customize your experience and AI recommendations
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div>
                  <Label>Preferred Languages</Label>
                  <Input
                    placeholder="e.g., JavaScript, Python, Go"
                    defaultValue={preferences?.preferredLanguages?.join(", ")}
                    onBlur={(e) => {
                      const languages = e.target.value.split(",").map(l => l.trim()).filter(l => l);
                      updatePreferencesMutation.mutate({ preferredLanguages: languages });
                    }}
                  />
                </div>

                <div>
                  <Label>Preferred Topics</Label>
                  <Input
                    placeholder="e.g., machine-learning, web-development, devops"
                    defaultValue={preferences?.preferredTopics?.join(", ")}
                    onBlur={(e) => {
                      const topics = e.target.value.split(",").map(t => t.trim()).filter(t => t);
                      updatePreferencesMutation.mutate({ preferredTopics: topics });
                    }}
                  />
                </div>

                <div>
                  <Label>Excluded Topics</Label>
                  <Input
                    placeholder="Topics to exclude from recommendations"
                    defaultValue={preferences?.excludedTopics?.join(", ")}
                    onBlur={(e) => {
                      const topics = e.target.value.split(",").map(t => t.trim()).filter(t => t);
                      updatePreferencesMutation.mutate({ excludedTopics: topics });
                    }}
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div>
                    <Label>AI Recommendations</Label>
                    <p className="text-sm text-muted-foreground">
                      Enable personalized AI repository recommendations
                    </p>
                  </div>
                  <Switch
                    checked={preferences?.aiRecommendations ?? true}
                    onCheckedChange={(checked) => {
                      updatePreferencesMutation.mutate({ aiRecommendations: checked });
                    }}
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div>
                    <Label>Email Notifications</Label>
                    <p className="text-sm text-muted-foreground">
                      Receive weekly repository recommendations via email
                    </p>
                  </div>
                  <Switch
                    checked={preferences?.emailNotifications ?? false}
                    onCheckedChange={(checked) => {
                      updatePreferencesMutation.mutate({ emailNotifications: checked });
                    }}
                  />
                </div>

                <Separator className="my-4" />

                <div className="flex items-center justify-between">
                  <div>
                    <Label>Product Tour</Label>
                    <p className="text-sm text-muted-foreground">
                      Take a guided tour to learn about all features
                    </p>
                  </div>
                  <Button
                    onClick={restartTour}
                    variant="outline"
                    className="gap-2"
                  >
                    <Sparkles className="w-4 h-4" />
                    Restart Tour
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/repository-detail.tsx.content:
import { useParams } from "wouter";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useAuth } from "@/hooks/useAuth";
import Header from "@/components/header";
import AnalysisResults from "@/components/analysis-results";
import { Comments } from "@/components/comments";
import { Ratings } from "@/components/ratings";
import { CollectionsManager } from "@/components/collections-manager";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

export default function RepositoryDetail() {
  const { id } = useParams<{ id: string }>();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user } = useAuth();

  const { data, isLoading, error } = useQuery<any>({
    queryKey: ['/api/repositories', id],
    enabled: !!id,
  });

  const saveRepositoryMutation = useMutation({
    mutationFn: async () => {
      await apiRequest('POST', '/api/saved-repositories', {
        repositoryId: id,
      });
    },
    onSuccess: () => {
      toast({
        title: "Repository Saved",
        description: "Repository has been added to your saved list.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/repositories', id] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to save repository. Please try again.",
        variant: "destructive",
      });
    },
  });

  const unsaveRepositoryMutation = useMutation({
    mutationFn: async () => {
      await apiRequest('DELETE', `/api/saved-repositories/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Repository Unsaved",
        description: "Repository has been removed from your saved list.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/repositories', id] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to unsave repository. Please try again.",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-dark">
        <Header />
        <div className="max-w-6xl mx-auto px-6 py-16">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-700 rounded w-96 mb-4"></div>
            <div className="h-4 bg-gray-700 rounded w-64 mb-8"></div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="space-y-4">
                <div className="h-32 bg-gray-700 rounded"></div>
                <div className="h-24 bg-gray-700 rounded"></div>
              </div>
              <div className="space-y-4">
                <div className="h-32 bg-gray-700 rounded"></div>
                <div className="h-24 bg-gray-700 rounded"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="min-h-screen bg-dark">
        <Header />
        <div className="max-w-6xl mx-auto px-6 py-16 text-center">
          <div className="w-16 h-16 rounded-full bg-gradient-to-r from-secondary to-accent flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-exclamation-triangle text-white text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold mb-4">Repository Not Found</h1>
          <p className="text-gray-400 mb-6">
            The repository you're looking for could not be found or may have been removed.
          </p>
          <Button
            onClick={() => window.history.back()}
            className="bg-primary hover:bg-secondary"
            data-testid="button-go-back"
          >
            Go Back
          </Button>
        </div>
      </div>
    );
  }

  const { repository, analysis, similar, isSaved } = data;

  return (
    <div className="min-h-screen bg-dark text-white">
      <Header />

      <div className="max-w-6xl mx-auto px-6 py-8">
        {/* Repository Header */}
        <Card className="bg-card border border-border mb-8">
          <CardContent className="p-8">
            <div className="flex items-start justify-between mb-6">
              <div className="flex items-center space-x-4">
                <div className="w-16 h-16 rounded-xl bg-gradient-to-r from-blue-500 to-purple-600 flex items-center justify-center">
                  <i className="fab fa-github text-white text-2xl"></i>
                </div>
                <div>
                  <h1 className="text-3xl font-bold gradient-text" data-testid="text-repository-name">
                    {repository.fullName}
                  </h1>
                  <p className="text-gray-400 mt-1" data-testid="text-repository-description">
                    {repository.description || 'No description available'}
                  </p>
                  <div className="flex items-center space-x-4 mt-2 text-sm">
                    <div className="flex items-center space-x-1">
                      <i className="fas fa-star text-yellow-500"></i>
                      <span data-testid="text-stars">{repository.stars?.toLocaleString()}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <i className="fas fa-code-branch text-gray-400"></i>
                      <span data-testid="text-forks">{repository.forks?.toLocaleString()}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <i className="fas fa-eye text-gray-400"></i>
                      <span data-testid="text-watchers">{repository.watchers?.toLocaleString()}</span>
                    </div>
                  </div>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <Button
                  onClick={() => isSaved ? unsaveRepositoryMutation.mutate() : saveRepositoryMutation.mutate()}
                  disabled={saveRepositoryMutation.isPending || unsaveRepositoryMutation.isPending}
                  variant="outline"
                  className={`border-2 ${isSaved ? 'border-primary bg-primary/10 text-primary' : 'border-border'} hover:border-primary transition-colors`}
                  data-testid="button-save-repository"
                  data-tour="track-button"
                >
                  <i className={`fas fa-bookmark ${isSaved ? 'text-primary' : 'text-gray-400'} mr-2`}></i>
                  {isSaved ? 'Saved' : 'Save'}
                </Button>
                {user && (
                  <CollectionsManager 
                    repositoryId={id} 
                    userId={user.id}
                    showCreateButton={false}
                  />
                )}
                <Button
                  onClick={() => window.location.href = `/compare?repo=${id}`}
                  variant="outline"
                  className="border-2 border-border hover:border-primary transition-colors"
                  data-testid="button-compare"
                >
                  <i className="fas fa-balance-scale text-gray-400 mr-2"></i>
                  Compare
                </Button>
                <Button
                  onClick={() => window.location.href = `/discover?repoId=${id}&repoUrl=${encodeURIComponent(repository.htmlUrl)}`}
                  variant="outline"
                  className="border-2 border-border hover:border-primary transition-colors"
                  data-testid="button-find-similar"
                >
                  <i className="fas fa-search text-gray-400 mr-2"></i>
                  Find Similar
                </Button>
                <Button
                  onClick={() => window.open(repository.htmlUrl, '_blank')}
                  className="bg-gradient-to-r from-primary to-secondary hover:from-secondary hover:to-primary"
                  data-testid="button-view-github"
                >
                  <i className="fab fa-github mr-2"></i>
                  View on GitHub
                </Button>
              </div>
            </div>

            {/* Technology Stack */}
            {repository.languages && Object.keys(repository.languages).length > 0 && (
              <div className="mb-6">
                <h3 className="text-lg font-semibold mb-3">Technology Stack</h3>
                <div className="flex flex-wrap gap-2">
                  {Object.entries(repository.languages)
                    .sort(([,a], [,b]) => (b as number) - (a as number))
                    .slice(0, 8)
                    .map(([language, percentage]) => (
                      <Badge
                        key={language}
                        variant="secondary"
                        className="bg-blue-500/20 text-blue-400 border border-blue-500/30"
                        data-testid={`badge-language-${language}`}
                      >
                        {language} ({((percentage as number) / 1000).toFixed(1)}%)
                      </Badge>
                    ))}
                </div>
              </div>
            )}

            {/* Topics */}
            {repository.topics && repository.topics.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold mb-3">Topics</h3>
                <div className="flex flex-wrap gap-2">
                  {repository.topics.slice(0, 10).map((topic: string) => (
                    <Badge
                      key={topic}
                      variant="outline"
                      className="border-gray-500 text-gray-400"
                      data-testid={`badge-topic-${topic}`}
                    >
                      {topic}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Analysis Results */}
        {analysis ? (
          <AnalysisResults 
            analysis={analysis} 
            repository={{
              name: repository.name,
              full_name: repository.fullName,
              description: repository.description,
              language: repository.language,
              stargazers_count: repository.stargazersCount,
              forks_count: repository.forksCount,
            }}
          />
        ) : (
          <Card className="bg-card border border-border mb-8">
            <CardContent className="p-8 text-center">
              <div className="w-16 h-16 rounded-full bg-gradient-to-r from-primary to-secondary flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-brain text-white text-2xl"></i>
              </div>
              <h3 className="text-2xl font-bold mb-2">No Analysis Available</h3>
              <p className="text-gray-400 mb-6">
                This repository hasn't been analyzed yet. Start an analysis to get insights.
              </p>
              <Button
                onClick={() => {
                  // Trigger analysis
                  window.location.href = `/?analyze=${encodeURIComponent(repository.htmlUrl)}`;
                }}
                className="bg-gradient-to-r from-primary to-secondary hover:from-secondary hover:to-primary"
                data-testid="button-analyze"
              >
                <i className="fas fa-brain mr-2"></i>
                Analyze Repository
              </Button>
            </CardContent>
          </Card>
        )}

        {/* Social Features - Comments and Ratings */}
        <Card className="bg-card border border-border mb-8">
          <CardContent className="p-6">
            <Tabs defaultValue="comments" className="w-full">
              <TabsList className="grid w-full grid-cols-2 bg-dark/50">
                <TabsTrigger value="comments" className="data-[state=active]:bg-primary/20">
                  <i className="fas fa-comments mr-2"></i>
                  Comments
                </TabsTrigger>
                <TabsTrigger value="ratings" className="data-[state=active]:bg-primary/20">
                  <i className="fas fa-star mr-2"></i>
                  Ratings
                </TabsTrigger>
              </TabsList>
              <TabsContent value="comments" className="mt-6">
                <Comments repositoryId={id!} />
              </TabsContent>
              <TabsContent value="ratings" className="mt-6">
                <Ratings repositoryId={id!} />
              </TabsContent>
            </Tabs>
          </CardContent>
        </Card>

        {/* Similar Repositories */}
        {similar && similar.length > 0 && (
          <Card className="bg-card border border-border">
            <CardContent className="p-8">
              <h3 className="text-2xl font-semibold mb-6">Similar Repositories</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {similar.map((item: any) => {
                  const similarRepo = item.similarRepository;
                  return (
                    <div
                      key={similarRepo.id}
                      className="bg-dark rounded-lg p-4 border border-border hover:border-primary/30 transition-colors cursor-pointer"
                      onClick={() => window.location.href = `/repository/${similarRepo.id}`}
                      data-testid={`card-similar-${similarRepo.id}`}
                    >
                      <div className="flex items-center space-x-3 mb-3">
                        <div className="w-8 h-8 rounded-lg bg-gradient-to-r from-green-500 to-teal-600 flex items-center justify-center">
                          <i className="fab fa-github text-white text-sm"></i>
                        </div>
                        <div>
                          <h4 className="font-medium" data-testid={`text-similar-name-${similarRepo.id}`}>
                            {similarRepo.name}
                          </h4>
                          <p className="text-sm text-gray-400">
                            Similarity: {Math.round(item.similarity * 100)}%
                          </p>
                        </div>
                      </div>
                      <p className="text-sm text-gray-300 line-clamp-2">
                        {similarRepo.description || 'No description available'}
                      </p>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

