export interface HealthIndicator {
  emoji: string;
  label: string;
  color: string;
  description: string;
}

export function getScoreHealthIndicator(score: number): HealthIndicator {
  if (score >= 90) {
    return {
      emoji: 'ðŸš€',
      label: 'Excellent',
      color: 'text-green-500',
      description: 'Outstanding performance'
    };
  } else if (score >= 75) {
    return {
      emoji: 'âœ¨',
      label: 'Great',
      color: 'text-emerald-500',
      description: 'Above average performance'
    };
  } else if (score >= 60) {
    return {
      emoji: 'ðŸ‘',
      label: 'Good',
      color: 'text-blue-500',
      description: 'Solid performance'
    };
  } else if (score >= 45) {
    return {
      emoji: 'âš¡',
      label: 'Fair',
      color: 'text-yellow-500',
      description: 'Room for improvement'
    };
  } else if (score >= 30) {
    return {
      emoji: 'âš ï¸',
      label: 'Needs Work',
      color: 'text-orange-500',
      description: 'Significant improvements needed'
    };
  } else {
    return {
      emoji: 'ðŸ”§',
      label: 'Critical',
      color: 'text-red-500',
      description: 'Major issues to address'
    };
  }
}

export function getMetricHealthIndicator(metricName: string, score: number): string {
  const indicators: Record<string, Record<string, string>> = {
    originality: {
      high: 'ðŸ’Ž', // Diamond for unique/innovative
      medium: 'ðŸŒŸ', // Star for somewhat unique
      low: 'ðŸ“‹' // Clipboard for common/standard
    },
    completeness: {
      high: 'âœ…', // Check mark for complete
      medium: 'ðŸ”¨', // Hammer for work in progress
      low: 'ðŸ—ï¸' // Construction for early stage
    },
    marketability: {
      high: 'ðŸ’°', // Money bag for high market potential
      medium: 'ðŸ“ˆ', // Chart for growing potential
      low: 'ðŸ“Š' // Basic chart for low potential
    },
    monetization: {
      high: 'ðŸ’¸', // Money with wings for high revenue potential
      medium: 'ðŸ’µ', // Dollar bill for moderate potential
      low: 'ðŸª™' // Coin for low potential
    },
    usefulness: {
      high: 'ðŸŽ¯', // Target for highly useful
      medium: 'ðŸ”§', // Wrench for moderately useful
      low: 'ðŸ“¦' // Box for limited use
    }
  };

  const metric = indicators[metricName.toLowerCase()];
  if (!metric) {
    return getScoreHealthIndicator(score).emoji;
  }

  if (score >= 70) {
    return metric.high;
  } else if (score >= 40) {
    return metric.medium;
  } else {
    return metric.low;
  }
}

export function getOverallHealth(scores: Record<string, number>): HealthIndicator {
  const avgScore = Object.values(scores).reduce((sum, score) => sum + score, 0) / Object.values(scores).length;
  return getScoreHealthIndicator(avgScore);
}

export function getTrendIndicator(trend: 'up' | 'down' | 'stable'): string {
  switch (trend) {
    case 'up':
      return 'ðŸ“ˆ';
    case 'down':
      return 'ðŸ“‰';
    case 'stable':
      return 'âž¡ï¸';
    default:
      return 'âž¡ï¸';
  }
}

export function getActivityLevel(commits: number, lastUpdate: Date): string {
  const daysSinceUpdate = Math.floor((Date.now() - lastUpdate.getTime()) / (1000 * 60 * 60 * 24));

  if (daysSinceUpdate <= 7 && commits > 20) {
    return 'ðŸ”¥'; // Very active
  } else if (daysSinceUpdate <= 30 && commits > 10) {
    return 'âš¡'; // Active
  } else if (daysSinceUpdate <= 90) {
    return 'ðŸŒ±'; // Moderate activity
  } else if (daysSinceUpdate <= 180) {
    return 'ðŸ‚'; // Low activity
  } else {
    return 'â„ï¸'; // Inactive/dormant
  }
}

